"use client";

import { SignUp } from "@/app/examples/intercepting-modal/_components/signUp";
import { Modal } from "../_components/modal";

export default function Page() {
  return (
    <Modal>
      <SignUp />
      <div className="mt-6 flex items-center justify-center flex-col gap-4">
        <p className="text-gray-300 text-sm text-center">
          üëç modal is kept when backwards or forwards navigation
        </p>
        <button
          type="button"
          onClick={() => {
            window.location.reload();
          }}
        >
          Reload
        </button>
      </div>
    </Modal>
  );
}

codes.@modal/_components/modal.tsx:
"use client";

import { useRouter } from "next/navigation";
import { type PropsWithChildren } from "react";

export function Modal({ children }: PropsWithChildren) {
  const router = useRouter();

  return (
    <div className="absolute top-0 w-full h-full bg-gray-800 bg-opacity-75 flex justify-center items-center">
      <div className="w-10/12 h-3/4  bg-slate-700 rounded-md p-6 relative">
        <button
          type="button"
          className="absolute right-2 top-1 hover:text-blue-300"
          onClick={() => {
            router.back();
          }}
        >
          close
        </button>
        <div className="mt-5 h-full">{children}</div>
      </div>
    </div>
  );
}

