codes._components/meta.tsx:
"use client";

import { useEffect, useState } from "react";

export function Meta() {
  const [meta, setMeta] = useState("");

  useEffect(() => {
    const title = document.querySelector("title")?.outerHTML;
    const description = document.querySelector(
      'meta[name="description"]',
    )?.outerHTML;

    setMeta([title, description].join("\n"));
  }, []);

  return (
    <div className="overflow-scroll py-4">
      <pre>{meta}</pre>
    </div>
  );
}

codes.dynamic/page.tsx:
import type { Metadata, ResolvingMetadata } from "next";
import { Meta } from "../_components/meta";

type Params = {
  params: { id: string };
  searchParams: Record<string, string | string[] | undefined>;
};

export async function generateMetadata(
  params: Params,
  parent: ResolvingMetadata,
): Promise<Metadata> {
  const parentInfo = await parent;
  const parentTitle = parentInfo.title?.absolute;
  const parentDescription = parentInfo.description;

  return {
    title: `[modified] ${parentTitle}`,
    description: `[modified] ${parentDescription}`,
  };
}

export default function Page() {
  return <Meta />;
}
