import { cookies, headers } from "next/headers";
import { NextRequest, NextResponse } from "next/server";
import { COOKIE_NAME } from "../constants";

export async function GET(req: NextRequest, res: NextResponse) {
  const referer = headers().get("referer");
  const cookie = cookies().get(COOKIE_NAME);
  const newCookie = cookie
    ? `${cookie.name}=${cookie.value}; Max-Age=2;`
    : `${COOKIE_NAME}=referer:${referer}__${Date.now()}; Max-Age=2;`;

  return NextResponse.json(
    { msg: newCookie },
    {
      headers: {
        "Set-Cookie": newCookie,
      },
    },
  );
}

codes.page.tsx:
"use client";

import { Boundary } from "@/app/_components/boundary";
import { Loading } from "@/app/_components/loading";
import { useFetch } from "@/app/_hooks/useFetch";
import { notFound } from "next/navigation";
import { COOKIE_NAME } from "./constants";

export default function Page() {
  const path = "/examples/route-handlers/api";
  const { data, loading, error } = useFetch<{ msg: string }>(path);

  if (loading) {
    return <Loading />;
  }

  if (!data || error) {
    return notFound();
  }

  return (
    <Boundary label="Page">
      <div className="flex flex-col gap-4">
        <div>
          <p>endpoint: {path}</p>
          <p>cookie name: {COOKIE_NAME}</p>
        </div>
        <p className="break-all text-gray-400">{data.msg}</p>
      </div>
    </Boundary>
  );
}
