import { z } from "zod";

// frontend can use this same schema with react-hook-form, etc..
export const schema = z.object({
  name: z.string().min(2, '"name" must be at least 2 characters'),
});

codes.page.tsx:
"use client";

import { useFormState, useFormStatus } from "react-dom";
import { create } from "./actions";

export default function Page() {
  const [state, formAction] = useFormState(create, null);

  return (
    <div className="flex flex-col gap-4">
      <form action={formAction} className="flex flex-col gap-4">
        <input type="text" name="name" className="bg-slate-600" />
        <Submit />
      </form>
      {state?.message && <p>{state?.message}</p>}
    </div>
  );
}

function Submit() {
  const { pending } = useFormStatus();

  return (
    <button
      type="submit"
      className={pending ? "text-gray-500" : "text-gray-300"}
    >
      Submit
    </button>
  );
}

codes.actions.ts:
"use server";

import { schema } from "./schema";

const initialState = {
  message: "",
};

export async function create(
  prevState: typeof initialState | null,
  formData: FormData,
) {
  const name = formData.get("name");
  const validatedFields = schema.safeParse({ name });

  if (!validatedFields.success) {
    return {
      message: `error: ${validatedFields.error.flatten().fieldErrors.name}`,
    };
  }

  // e.g. insert to DB
  await new Promise((resolve) => setTimeout(resolve, 1000));

  return {
    message: `success: ${formData.get("name")}`,
  };
}
