codes.page.tsx:
import { Boundary } from "@/app/_components/boundary";
import { Link } from "@/app/_components/link";

export default function Page() {
  return (
    <Boundary label="Page">
      <div className="flex flex-col gap-4">
        <p>There are two types of rendering methods</p>
        <ul className="list-disc list-inside">
          <li>Static Rendering (Default)</li>
          <li>Dynamic Rendering</li>
        </ul>
        <Link href="/examples/server-components/only-static">Only Static</Link>
        <Link href="/examples/server-components/static-dynamic">
          Static and Dynamic
        </Link>
      </div>
    </Boundary>
  );
}

codes.static-dynamic/page.tsx:
import { Boundary } from "@/app/_components/boundary";
import { Dynamic } from "../_components/dynamic";
import { Static } from "../_components/static";

export default function Page() {
  return (
    <Boundary label="Static and Dynamic">
      <p>
        During rendering, if a dynamic function or uncached data request is
        discovered, Next.js will switch to dynamically rendering the whole
        route.
      </p>
      <Static />
      <Dynamic />
    </Boundary>
  );
}
