import { Suspense } from "react";
import { ImageItem, ImageList } from "../../_components/imageList";
import { Section } from "../../_components/section";
import { ImageSkeletons } from "../../_components/skeleton";
import { isSignIn } from "../../_utils/auth";
import { items } from "../../mock";

export default async function Page() {
  const signedIn = await isSignIn();

  if (!signedIn) {
    return null;
  }

  return (
    <Section title="Your Cart">
      <Suspense fallback={<ImageSkeletons num={3} />}>
        <Items />
      </Suspense>
    </Section>
  );
}

async function Items() {
  // e.g. fetch
  const allItems = await new Promise<(typeof items)[number][]>((resolve) => {
    setTimeout(() => resolve(items.filter(({ id }) => id === 1)), 3000);
  });

  return (
    <ImageList>
      {allItems.map(({ id, thumbnail }) => (
        <ImageItem id={id} thumbnail={thumbnail} key={id} />
      ))}
    </ImageList>
  );
}

import { getItem } from "@/app/examples/(showcases)/service-e-commerce/_utils/items";
import { type Item } from "@/app/examples/(showcases)/service-e-commerce/mock";
import { notFound } from "next/navigation";
import { Suspense } from "react";
import { Dialog } from "../../_components/dialog";

type Props = {
  params: {
    itemId: string;
  };
};

export default function Page({ params }: Props) {
  return (
    <Dialog>
      <Suspense fallback={<Loading />}>
        <Main id={params.itemId} />
      </Suspense>
    </Dialog>
  );
}

async function Main({ id }: { id: string }) {
  const item = await getItem(id);

  if (!item) {
    return notFound();
  }

  return <Container {...item} />;
}

function Loading() {
  return <Container id={0} name="loading..." thumbnail="🐕" description="" />;
}

function Container({ name, description, thumbnail }: Item) {
  return (
    <div className="flex flex-col gap-8">
      <div className="flex gap-4 items-center">
        <span className="text-4xl">{thumbnail}</span>
        <h2>{name}</h2>
      </div>
      <p>{description}</p>
    </div>
  );
}

